<?phprequire("../admin/session.php");		function arreglo_libros(){					$local["arreglo"] =array();								$libros = file('libros.txt');				for ($i = 0; $i < count($libros); $i++) {				$tmp = explode('|', $libros[$i]);											$local["arreglo"][$i] = array("Codigo"=>$tmp[0],"Titulo"=>$tmp[1],"Autor"=>$tmp[2],"Editorial"=>$tmp[3],"Coleccion"=>$tmp[4],"NroColeccion"=>$tmp[5],"Formato"=>$tmp[6],"Edicion"=>$tmp[7],"Tomo"=>$tmp[8],"Precio"=>$tmp[9],"Existencia"=>$tmp[10],"isbn"=>$tmp[11],"barras"=>$tmp[12]);								}						return $local["arreglo"];				}				function paginar($v, $l, $p,$pag) { 				// DEFINIMOS LA CANTIDAD DE PÁGINAS 		$paginas = ceil(count($v) / $l); 				// CONDICION DE INICIO 		$inicio = ($p-1)*$l;        		// CONDICION DE FINAL 		$final = $p*$l; 		// MOSTRAMOS LOS ITEMS RESPECTIVOS 		echo '<div id="resultados">'; 		for ($i=$inicio; $i<$final; $i++) {          if (isset($v[$i]))             echo "<div id=\"item\">$i.- $v[$i]</div>";          else              break; 		} 		echo '</div>'; 	  		// LISTAMOS LAS PÁGINAS 		echo '<div id="paginas">'; 		if ($p>1) 			echo "<a href=\"$pag?p=" . ($p-1) . "\">P&aacute;gina Anterior</a>&nbsp;-&nbsp;";        		for ($i=1; $i<=$paginas; $i++) {          if ($i == $p)             echo "<strong>$i</strong>&nbsp;";          else              echo "<a href=\"$pag?p=$i\">$i</a>&nbsp;"; 		}        		if ($p<$paginas) 			echo "&nbsp;-&nbsp;<a href=\"$pag?p=" . ($p+1) . "\">P&aacute;gina Siguiente&nbsp;</a>"; 		echo '</div>'; 		return; 		} ?>
